import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../App.css";
import logo from "../../images/ezpark.png";

export default function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const [showModal, setShowModal] = useState(false);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://localhost:5000/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password }),
      });

      const data = await response.json();

      if (response.ok) {
        setMessage("✅ Login Successful!");
        setShowModal(true);
      } else {
        setMessage(data.message || "❌ Invalid Credentials");
        setShowModal(true);
      }
    } catch (error) {
      console.error("Error:", error);
      setMessage("⚠️ Server Error. Try again later.");
      setShowModal(true);
    }
  };

  return (
    <div className="ez-login-page">
      {/* Top blue bar with logo */}
      <header className="ez-topbar">
        <div className="ez-topbar-inner">
          <img src={logo} alt="EzPark" className="ez-logo" />
        </div>
      </header>

      <main className="ez-main">
        <section className="ez-lefttext">
          <h1>
            Welcome to the
            <br />
            Parking Management
            <br />
            System
          </h1>
        </section>

        <aside className="ez-card">
          <form className="ez-form" onSubmit={handleLogin}>
            <label className="ez-label">Username:</label>
            <input
              className="ez-input"
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />

            <label className="ez-label">Password</label>
            <input
              className="ez-input"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />

            <div
              className="ez-forgot"
              role="button"
              onClick={() => navigate("/forgot-password")}
            >
              Forgot password?
            </div>

            <button className="ez-login-btn" type="submit">
              LOGIN
            </button>
          </form>
        </aside>
      </main>

      {/* center modal popup */}
      {showModal && (
        <div className="ez-modal-overlay" onClick={() => setShowModal(false)}>
          <div
            className="ez-modal"
            onClick={(e) => {
              e.stopPropagation();
            }}
          >
            <p>{message}</p>
            <button className="ez-close" onClick={() => setShowModal(false)}>
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
