import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { FaUsers, FaCar, FaChartBar, FaClock, FaSignOutAlt } from "react-icons/fa";
import logo from "../images/ezpark.png";
import "../css/Admin.css";

function AdminDashboard() {
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/", { replace: true });
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/", { replace: true });
  };

  return (
    <div className="ez-dashboard-page">
      {/* ✅ Topbar reused */}
      <div className="ez-topbar">
        <div className="ez-topbar-inner">
          <img src={logo} alt="EZPark Logo" className="ez-logo" />
        </div>
      </div>

      {/* ✅ Dashboard card */}
      <div className="dashboard-container">
        <div className="dashboard-card">
          <h1 className="dashboard-title">Admin Dashboard</h1>

          <div className="dashboard-grid">
            <button className="dashboard-btn">
              <FaUsers /> Manage Staff
            </button>

            <button className="dashboard-btn">
              <FaCar /> Manage Parking
            </button>

            <button className="dashboard-btn">
              <FaChartBar /> Reports
            </button>

            <button className="dashboard-btn">
              <FaClock /> Activity Logs
            </button>

            <button className="dashboard-btn logout-btn" onClick={handleLogout}>
              <FaSignOutAlt /> Log out
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AdminDashboard;
