import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../App.css";
import logo from "../images/ezpark.png";

function Login() {
  const express = require("express");
const mongoose = require("mongoose");
const cors = require("cors");
const app = express();
app.use(express.json());
app.use(cors());

// Example User schema
const userSchema = new mongoose.Schema({
  username: String,
  password: String, // For production, hash the password with bcrypt!
});
const User = mongoose.model("User", userSchema);

// Login route
app.post("/api/auth/login", async (req, res) => {
  const { username, password } = req.body;

  try {
    // find user in database
    const user = await User.findOne({ username });

    if (!user) {
      // username not found
      return res.status(401).json({ success: false, message: "Invalid credentials" });
    }

    // check password (plain text, for demo; use bcrypt for production)
    if (user.password !== password) {
      return res.status(401).json({ success: false, message: "Invalid credentials" });
    }

    // success
    return res.status(200).json({ success: true, message: "Login successful", user });
  } catch (err) {
    console.error(err);
    return res.status(500).json({ success: false, message: "Server error" });
  }
});

// Connect to MongoDB and start server
mongoose.connect("mongodb://localhost:27017/ezpark", { useNewUrlParser: true, useUnifiedTopology: true })
  .then(() => {
    console.log("DB connected");
    app.listen(5000, () => console.log("Server running on port 5000"));
  })
  .catch(err => console.error(err));


  return (
    <div className="ez-login-page">
      {/* Top Navigation */}
      <div className="ez-topbar">
        <div className="ez-topbar-inner">
          <img src={logo} alt="EZPark Logo" className="ez-logo" />
        </div>
      </div>

      {/* Main Section */}
      <div className="ez-main">
        {/* Welcome Text */}
        <div className="ez-lefttext">
          <h1>
            Welcome to <br /> EZ Park System
          </h1>
        </div>

        {/* Login Card */}
        <div className="ez-card">
          <form className="ez-form" onSubmit={handleLogin}>
            <label className="ez-label">Username</label>
            <input
              type="text"
              className="ez-input"
              placeholder="Enter your username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />

            <label className="ez-label">Password</label>
            <input
              type="password"
              className="ez-input"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />

            {/* Forgot Password */}
            <button
              type="button"
              className="forgot-btn"
              onClick={() => navigate("/forgot-password")}
            >
              Forgot Password?
            </button>

            {/* Login Button */}
            <button type="submit" className="ez-login-btn">
              LOGIN
            </button>
          </form>
        </div>
      </div>

      {/* Modal */}
      {showModal && (
        <div className="ez-modal-overlay">
          <div className="ez-modal">
            <p>{message}</p>
            <button onClick={() => setShowModal(false)} className="ez-close">
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Login;
