import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { FaClock, FaArrowRight, FaSignOutAlt } from "react-icons/fa";
import logo from "../images/ezpark.png"; // ✅ Import your logo
import "../css/Staff.css";

function StaffDashboard() {
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/", { replace: true });
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/", { replace: true });
  };

  return (
    <div className="dashboard-card">
  <h1 className="dashboard-title">Welcome Back</h1>

  <div className="dashboard-buttons">
    <button className="dashboard-btn"><FaVideo /> CCTV</button>
    <button className="dashboard-btn"><FaMap /> Layout</button>
    <button className="dashboard-btn"><FaFileAlt /> Report</button>
    <button className="dashboard-btn logout-btn" onClick={handleLogout}>
      <FaSignOutAlt /> Log out
    </button>
  </div>
</div>

  );
}

export default StaffDashboard;
