import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { FaClock, FaArrowRight, FaSignOutAlt } from "react-icons/fa";
import "../css/Spp.css";

function StaffDashboard() {
  const navigate = useNavigate();

  useEffect(() => {
    // ✅ If no token, redirect to login
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/", { replace: true });
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");

    // ✅ Replace prevents going back
    navigate("/", { replace: true });
  };

  return (
    <div className="staff-dashboard">
      <div className="dashboard-container">
        <h1 className="dashboard-title">Welcome Back</h1>

        <button className="dashboard-btn timein-btn">
          <FaClock /> Time - In
        </button>

        <button className="dashboard-btn checkout-btn">
          <FaArrowRight /> Check - Out
        </button>

        <button className="dashboard-btn logout-btn" onClick={handleLogout}>
          <FaSignOutAlt /> Logout
        </button>
      </div>
    </div>
  );
}

export default StaffDashboard;
